INSERT INTO `email_templates` (`id`, `type`, `slug`, `subject`, `body`, `created_at`, `updated_at`) VALUES
(1,	'Welcome Mail',	'welcome-mail',	'Welcome To MtgPro.me',	'<p>Hey&nbsp;<span style=\"color: var(--bs-card-color); font-family: var(--tblr-font-sans-serif); font-size: 0.875rem; letter-spacing: 0px; text-align: var(--bs-body-text-align);\">{{user_name}}!</span></p><p>Thanks for signing up to {{site_name}}! Make sure you link all your social accounts and websites to your PROfile under \"My Cards\" in your dashboard.</p><p>If you have any questions, please visit our website <a href=\"https://mtgpro.me/\" target=\"_blank\">{{site_name}}</a>&nbsp;or email info@mtgpro.me</p><p><br></p>',	NULL,	'2023-03-17 07:42:00'),
(2,	'Plan Purchase',	'plan-purchase',	'Thank You for order',	'<p>Hello! {{user_name}}, thank you for purchasing from {{site_title}}. \r\n</p><p>Total order {{order_cost}}, </p><p>Transaction number {{transaction_number}}.\r\n</p><p>Your order is now {{order_status}}.\r\n</p>\r\n\r\n<p>Thanking for with us</p>',	NULL,	'2023-02-13 11:16:53'),
(3,	'Plan Upgrade',	'plan-upgrade',	'Plan Upgrade Info',	'<p>Hello! {{user_name}}, thank you for purchasing from {{site_title}}.</p><p>Total order {{order_cost}},</p><p>Transaction number {{transaction_number}}.</p><p>Your order is now {{order_status}}.</p><p>Thanking for with us.</p>',	NULL,	'2023-02-13 13:34:27'),
(5,	'Greetings to Visitor',	'send-connect-to-visitors-cc-subscriber',	'Greetings to Visitor',	'<p>You have connected with {{user_name}} by submitting the form.</p><p>Thank you for your interest.&nbsp;</p><p><span style=\"color: var(--bs-card-color); font-family: var(--tblr-font-sans-serif); font-size: 0.875rem; letter-spacing: 0px; text-align: var(--bs-body-text-align);\">Thanking with&nbsp;</span><span style=\"color: var(--bs-card-color); font-family: var(--tblr-font-sans-serif); font-size: 0.875rem; letter-spacing: 0px; text-align: var(--bs-body-text-align);\">{{site_title}}</span><br></p>',	NULL,	'2023-02-13 14:17:21'),
(7,	'Request Features to Subscriber Mail',	'request-features-to-subscriber-mail',	'Request Features Submited',	'Hello {{user_name}}, \nThanks for Suggest.\nWe Care About Our Customers.',	NULL,	'2023-02-01 13:14:17'),
(8,	'Contact query mail to card owner',	'contact-query-mail-to-card-owner',	'You Have A New MTGPRO Connection.',	'<p>Hello, {{owner}},\r\n</p><p>You have a connection from {{name}}.</p><p>\r\n\r\nHere are some of the details.\r\n</p><p>Name  : {{name}}\r\n</p><p>Email : {{email}}\r\n</p><p>Phone :  {{phone}}</p><p>\r\nJob title : {{title}}\r\n</p><p>Company Name: {{company_name}}</p><p>Message : {{message}}\r\n\r\n</p><p><span style=\"color: var(--bs-card-color); font-family: var(--tblr-font-sans-serif); font-size: 0.875rem; letter-spacing: 0px; text-align: var(--bs-body-text-align);\">Show&nbsp;</span><span style=\"color: var(--bs-card-color); font-family: var(--tblr-font-sans-serif); font-size: 0.875rem; letter-spacing: 0px; text-align: var(--bs-body-text-align);\">{{name}} you are a PRO and reach out as soon as possible.</span><br></p>',	'2023-02-01 13:13:21',	'2023-03-02 21:06:16'),
(9,	'Admin plan Change',	'admin-plan-change',	'Admin plan change notify',	'<p>Hello Users {{subcriber}},</p><p>Admin change some something in the plans,</p><p>Here the changing details of the plan,</p><p>Plan Name : {{plan_name}}</p><p>Plan Description : {{plan_description}}<br></p><p>Monthly Price : {{currency}}{{plan_price_monthly}}<br></p><p>Monthly Price : {{currency}}{{plan_price_yearly}}</p><p>Number Of Cards :&nbsp; {{number_of_cards}}</p><p>Plan features :</p><p>{{plan_feature}}<br></p>',	'2023-02-02 05:55:18',	'2023-02-02 09:04:53'),
(10,	'Password Reset',	'password-reset',	'Password Reset',	'<p><br>Hi {{name}},<br><br>There was a request to change your password!<br><br>If you did not make this request then please ignore this email.<br><br>Otherwise, please click this link to change your password:<a href=\"{{link}}\" target=\"_blank\">Reset Link</a><br></p>',	'2023-02-02 10:40:49',	'2023-02-02 11:10:41'),
(11,	'Account Deactivation',	'account-delatation',	'Account Deactivation',	'<h1 style=\"box-sizing: border-box; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; position: relative; color: #3d4852; font-size: 18px; font-weight: bold; margin-top: 0; text-align: left;\">\n                                            Hello!</h1>\n                                        <p style=\"box-sizing: border-box; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; position: relative; font-size: 16px; line-height: 1.5em; margin-top: 0; text-align: left;\">\n                                            Itâ€™s sad to see you go. As requested, your account has been deleted. No\n                                            further action on your part is required.</p>\n                                        <p style=\"box-sizing: border-box; font-family: -apple-system, BlinkMacSystemFont, \'Segoe UI\', Roboto, Helvetica, Arial, sans-serif, \'Apple Color Emoji\', \'Segoe UI Emoji\', \'Segoe UI Symbol\'; position: relative; font-size: 16px; line-height: 1.5em; margin-top: 0; text-align: left;\"><br></p>',	'2023-02-02 11:17:05',	'2023-02-02 11:18:24'),
(12,	'Order Status Change',	'order-status-change',	'Order Status Change',	'<p>Hello! {{user_name}}. \r\n\r\n</p><p>Your order is now {{order_status}}.</p><p>Your order number is {{order_number}}&nbsp;</p><p>\r\n</p>\r\n\r\n<p>Thank you for being and MTG PRO!</p>',	'2023-02-02 12:57:08',	'2023-02-28 09:09:27'),
(13,	'Product Purchase',	'product-purchase',	'Thank You for order',	'<p>Hello! {{user_name}}, thank you for purchasing from {{site_title}}. \r\n</p><p>Total order cost: {{order_cost}}, </p><p>Transaction number {{transaction_number}}.\r\n</p><p>Your order is now {{order_status}}.\r\n</p>\r\n\r\n<p>Thank you for being an MTG PRO!</p>',	NULL,	'2023-02-28 09:10:13');
