(14,	'MTG PRO - PROfob',	'mtg-pro-profob14',	'/productThumb/profob_front-removebg-preview-63d6d9f73a245.png',	'<p><span style=\"font-size: 14px;\">ï»¿</span>A sleek &amp; durable fob that you can&nbsp; loop onto virtually anything! Many of our PROs secure their PROfob to their keys but some have also secured it to different zippers, wallets and gym bags!</p>',	20900,	21900,	2,	0,	1,	'2023-01-29 20:08:58',	'2023-01-30 02:41:27',	0.00,	91,	91),
(15,	'PROchain - White',	'prochain-white15',	'/productThumb/prochain_white-removebg-preview-63e1bd95374f9.png',	'<p><span style=\"font-size: 14px;\">ï»¿</span><span style=\"font-size: 14px;\">ï»¿</span>The PROchain -&nbsp; White. A simple, lightweight keychain with your PROfile embedded inside to quickly tap against anyone\'s phone and let them see realize you are the MTGPRO they need to work with.</p>',	34800,	35800,	1,	0,	1,	'2023-01-29 20:20:52',	'2023-02-07 08:55:17',	0.00,	91,	91),
